"use client"

import { useSimulateDepositETH } from "op-wagmi"
import * as React from "react"



interface BalanceProps
  extends Omit<React.HTMLAttributes<HTMLDivElement>, "children"> {
    to: `0x${string}`
    amount: bigint
    l2ChainId: number
    displayLoading?: boolean
    displayError?: boolean
}



const SimulateDepositETH = React.forwardRef<HTMLDivElement, BalanceProps>(
  (
    {
      className,
      to,
      amount,
      l2ChainId,
      displayLoading = true,
      displayError = true,
      ...props
    },
    ref
  ) => {
const result = useSimulateDepositETH({
  args:{
    to,
    amount,

},l2ChainId
  })



 return <div {...props}>{result}</div>
  }
)

SimulateDepositETH.displayName = "SimulateDepositETH"

export { SimulateDepositETH }
